using System.Globalization;
using System.Windows;
using RealEstateListingViewer.Models;
using RealEstateListingViewer.Services;
using RealEstateListingViewer.Views;
using System.Threading;

namespace RealEstateListingViewer
{
    /// <summary>
    /// Interaction logic for App.xaml
    /// </summary>
    public partial class App : Application
    {
        protected override void OnStartup(StartupEventArgs e)
        {
            base.OnStartup(e);

            Thread.CurrentThread.CurrentCulture = new CultureInfo("nl-NL");
            Thread.CurrentThread.CurrentUICulture = new CultureInfo("nl-NL");

            var model = new RealEstateListingPresentationModel();

            ((Window)model.View).Show();
        }
    }
}
